// Party.js
import * as React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import Avatar from "@mui/material/Avatar";
import { Typography } from "@mui/material";
import { motion } from "framer-motion";
import EventInviation from "./EventInvitation";

const tabData = [
  { label: "Birthday", icon: "https://cdn-icons-png.flaticon.com/512/4549/4549811.png" },
  { label: "Wedding", icon: "https://media.istockphoto.com/id/1072540494/vector/wedding-rings-icon.jpg?s=612x612" },
  { label: "House Warming", icon: "https://media.istockphoto.com/id/1316642892/vector/home-sweet-home-black-and-white-image-of-a-house-with-a-quote.jpg?s=612x612" },
  { label: "Baby Shower", icon: "https://cdn-icons-png.flaticon.com/512/8338/8338143.png" },
];

export default function Party() {
  const [currentTabIndex, setCurrentTabIndex] = React.useState(0);

  const handleTabChange = (e, tabIndex) => {
    setCurrentTabIndex(tabIndex);
  };

  const currentEventType = tabData[currentTabIndex].label;

  return (
    <div style={{ display: "flex", justifyContent: "center", flexDirection: "column", alignItems: "center" }}>
      <Box sx={{ maxWidth: { xs: 320, sm: 1500 }, bgcolor: "background.paper", width: "100%" }}>
        <Box sx={{ display: "flex", justifyContent: "center" }}>
          <Tabs
            value={currentTabIndex}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons="auto"
            aria-label="scrollable auto tabs"
          >
            {tabData.map((tab, index) => (
              <Tab
                key={tab.label}
                icon={
                  <motion.div whileHover={{ scale: 1.1, transition: { duration: 0.3 } }}>
                    <Avatar alt={tab.label} src={tab.icon} sx={{ width: 80, height: 90 }} />
                  </motion.div>
                }
              />
            ))}
          </Tabs>
        </Box>

        <Box sx={{ mt: 4, textAlign: "center" }}>
          <Typography variant="h5">{currentEventType}</Typography>
          <EventInviation selectedEventType={currentEventType} />
        </Box>
      </Box>
    </div>
  );
}
