import * as React from "react";
import { Container, Grid, Button } from "@mui/material";
import EventInvitation from "./EventInvitation";

export default function Party() {
  const [selectedEventType, setSelectedEventType] = React.useState("");

  // Handle tab change
  const handleTabChange = (event, newValue) => {
    setSelectedEventType(newValue);
  };

  return (
    <Container maxWidth="lg" sx={{ py: 5 }}>
      <Grid container spacing={4} justifyContent="center">
        {/* Party tabs - Buttons to simulate tab change */}
        <Grid item xs={12} textAlign="center">
          <Button
            variant="contained"
            onClick={(e) => handleTabChange(e, "Birthday")}
            sx={{ mr: 2 }}
          >
            Birthday
          </Button>
          <Button
            variant="contained"
            onClick={(e) => handleTabChange(e, "Wedding")}
            sx={{ mr: 2 }}
          >
            Wedding
          </Button>
          <Button
            variant="contained"
            onClick={(e) => handleTabChange(e, "House Warming")}
            sx={{ mr: 2 }}
          >
            House Warming
          </Button>
          <Button
            variant="contained"
            onClick={(e) => handleTabChange(e, "Baby Shower")}
            sx={{ mr: 2 }}
          >
            Baby Shower
          </Button>
        </Grid>

        {/* EventInvitation component, passing selectedEventType as a prop */}
        <Grid item xs={12}>
          <EventInvitation
            selectedEventType={selectedEventType}
            setSelectedEventType={setSelectedEventType}
          />
        </Grid>
      </Grid>
    </Container>
  );
}
