import * as React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import Avatar from "@mui/material/Avatar";
import { Typography } from "@mui/material";
import { motion } from "framer-motion";
import EventInvitation from "./EventInvitation"; // Make sure the component name is correct

export default function Party() {
  const [currentTabIndex, setCurrentTabIndex] = React.useState(0);

  // Array of event types for easy reference
  const eventTypes = ["Birthday", "Wedding", "House Warming", "Baby Shower"];

  // Handle tab change and update the selected event type
  const handleTabChange = (e, tabIndex) => {
    setCurrentTabIndex(tabIndex);
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <Box
        sx={{
          maxWidth: { xs: 320, sm: 1500 },
          bgcolor: "background.paper",
          width: "100%",
        }}
      >
        <Box sx={{ display: "flex", justifyContent: "center" }}>
          <Tabs
            value={currentTabIndex}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons="auto"
            aria-label="scrollable auto tabs example"
          >
            <Tab
              icon={
                <motion.div
                  whileHover={{
                    scale: 1.1,
                    transition: { duration: 0.3 },
                  }}
                >
                  <Avatar
                    alt="birthday"
                    src="https://cdn-icons-png.flaticon.com/512/4549/4549811.png"
                    sx={{ width: 80, height: 90 }}
                  />
                </motion.div>
              }
            />
            <Tab
              icon={
                <motion.div
                  whileHover={{
                    scale: 1.1,
                    transition: { duration: 0.3 },
                  }}
                >
                  <Avatar
                    alt="wedding"
                    src="https://media.istockphoto.com/id/1072540494/vector/wedding-rings-icon.jpg?s=612x612&w=0&k=20&c=8g6Kh2hwUqJPW_EXAtPVeEUSNyD-h1GD4EiWc2QtG9g="
                    sx={{ width: 80, height: 90 }}
                  />
                </motion.div>
              }
            />
            <Tab
              icon={
                <motion.div
                  whileHover={{
                    scale: 1.1,
                    transition: { duration: 0.3 },
                  }}
                >
                  <Avatar
                    alt="house-warming"
                    src="https://media.istockphoto.com/id/1316642892/vector/home-sweet-home-black-and-white-image-of-a-house-with-a-quote.jpg?s=612x612&w=0&k=20&c=BKCCbt5M1INcSkRPQT4SWmIFuG-jrcr_bvgqGFKM-D0="
                    sx={{ width: 80, height: 90 }}
                  />
                </motion.div>
              }
            />
            <Tab
              icon={
                <motion.div
                  whileHover={{
                    scale: 1.1,
                    transition: { duration: 0.3 },
                  }}
                >
                  <Avatar
                    alt="baby-shower"
                    src="https://cdn-icons-png.flaticon.com/512/8338/8338143.png"
                    sx={{ width: 80, height: 90 }}
                  />
                </motion.div>
              }
            />
          </Tabs>
        </Box>

        <Box sx={{ mt: 4, textAlign: "center" }}>
          {/* Passing selected event type to EventInvitation as a prop */}
          <Typography variant="h5">{eventTypes[currentTabIndex]}</Typography>
          <EventInvitation selectedEventType={eventTypes[currentTabIndex]} />
        </Box>
      </Box>
    </div>
  );
}
