import React, { useEffect, useState } from "react";
import { TextField, MenuItem, Stack, Container, Typography } from "@mui/material";

const eventTypeOptions = [
  { value: "Birthday" },
  { value: "Wedding" },
  { value: "House Warming" },
  { value: "Baby Shower" },
];

const eventBackgrounds = {
  Birthday: "https://images.greetingsisland.com/images/envelopes/preview/p-envelope81-b.png",
  Wedding: "https://www.pngmart.com/files/6/Hand-drawn-Wedding-Invitation-Background-PNG.png",
  "House Warming": "https://img.freepik.com/free-vector/housewarming-celebration-card-template_23-2148955947.jpg",
  "Baby Shower": "https://img.freepik.com/free-vector/flat-baby-shower-background_23-2148947601.jpg",
};

export default function EventInvitation({ selectedEventType, onDropdownChange }) {
  const [eventData, setEventData] = useState({
    guestName: "",
    guestEmail: "",
    phone: "",
    eventType: selectedEventType,
    eventDate: "",
    time: "",
    description: "",
  });

  useEffect(() => {
    setEventData((prev) => ({
      ...prev,
      eventType: selectedEventType,
    }));
  }, [selectedEventType]);

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  return (
    <Container maxWidth="lg" sx={{ py: 5 }}>
      <Typography variant="h5" textAlign="center" mb={4}>
        Event Details for {selectedEventType}
      </Typography>

      {/* Event Type Dropdown */}
      <Stack spacing={2}>
        <TextField
          select
          label="Event Type"
          name="eventType"
          value={eventData.eventType}
          onChange={(e) => {
            handleChange(e);
            onDropdownChange(e); // Trigger the parent component's dropdown change handler
          }}
          fullWidth
        >
          {eventTypeOptions.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.value}
            </MenuItem>
          ))}
        </TextField>
      </Stack>

      {/* Event Background */}
      <Box
        sx={{
          mt: 4,
          backgroundImage: `url(${eventBackgrounds[selectedEventType]})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "200px",
        }}
      ></Box>

      {/* Other fields like guest details, event date, etc. */}
      <Stack spacing={2} mt={3}>
        <TextField
          label="Guest Name"
          name="guestName"
          value={eventData.guestName}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Guest Email"
          name="guestEmail"
          value={eventData.guestEmail}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Phone Number"
          name="phone"
          value={eventData.phone}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Event Date"
          name="eventDate"
          value={eventData.eventDate}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Event Time"
          name="time"
          value={eventData.time}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Event Description"
          name="description"
          value={eventData.description}
          onChange={handleChange}
          fullWidth
        />
      </Stack>
    </Container>
  );
}
